mapper.select:
SELECT	*
FROM	GOODS



			SORT IN('general' , 'musical' , 'theater' , 'classic', 'consert') AND PART IN('ing', 'tobe', 'signoff') AND GENRE IN('nothing', 'romance', 'comedy', 'drama', 'horror', 'thriller', 'fantasy', 'historical') AND LOCATION IN('전국', '서울', '대학로', '홍대', '경기', '인천', '대전')


			SORT = #{sort} AND PART = #{part}


			SORT = #{sort} AND GENRE = #{genre}


			SORT = #{sort} AND LOCATION = #{location}
