mapper.update:
UPDATE	CONTACT_BOARD
SET		READ_CNT = READ_CNT + 1
WHERE	BOARD_ID = #{boardId}

mapper.select:
SELECT	PASSWD AS passwd
FROM	CONTACT_BOARD
WHERE	BOARD_ID = #{boardId}

SELECT	COUNT(*)
FROM	CONTACT_REPLY
WHERE	BOARD_ID = #{boardId}

SELECT	PASSWD AS passwd
FROM	CONTACT_REPLY
WHERE	REPLY_ID = #{replyId}

mapper.update:
UPDATE	CONTACT_REPLY
SET		ADMIN_ID = #{adminId},
		MEMBER_ID = #{memberId},
		CONTENT = #{content}
WHERE	REPLY_ID = #{replyId}
